{% set weather = states('weather.home') %}
{% set season = 'winter' if now().month in [11, 12, 1, 2, 3] else 'summer' %}
{% set hour = now().hour %}

Lux: {{ lux }}
Weather: {{ weather }}
Season: {{ season }}
Hour: {{ hour }}

--- Calculation ---
Lux boost: {% if lux < 10 %}18{% elif lux < 50 %}10{% elif lux < 150 %}5{% elif lux < 300 %}2{% else %}0{% endif %}
Weather boost: {{ {'fog': 20, 'pouring': 18, 'hail': 18, 'snowy': 15, 'snowy-rainy': 15, 'rainy': 12, 'lightning-rainy': 12, 'cloudy': 10, 'lightning': 8, 'partlycloudy': 5, 'windy': 2, 'windy-variant': 2, 'exceptional': 15}.get(weather, 0) }}
Season boost: {% if season == 'winter' %}8{% else %}0{% endif %}
Time modifier: {% if 22 <= hour or hour <= 6 %}0 (zeroed - night){% elif 6 < hour <= 8 or 18 <= hour < 22 %}0.7x (transition){% else %}1.0x (full){% endif %}

--- Raw total before time modifier ---
{% set base = 0 %}
{% if lux < 10 %}{% set base = base + 18 %}{% elif lux < 50 %}{% set base = base + 10 %}{% elif lux < 150 %}{% set base = base + 5 %}{% elif lux < 300 %}{% set base = base + 2 %}{% endif %}
{% set base = base + {'fog': 20, 'pouring': 18, 'hail': 18, 'snowy': 15, 'snowy-rainy': 15, 'rainy': 12, 'lightning-rainy': 12, 'cloudy': 10, 'lightning': 8, 'partlycloudy': 5, 'windy': 2, 'windy-variant': 2, 'exceptional': 15}.get(weather, 0) %}
{% if season == 'winter' %}{% set base = base + 8 %}{% endif %}
Raw total: {{ base }}

--- Final boost ---
{% if 22 <= hour or hour <= 6 or lux > 2500 %}
Final: 0 (zeroed)
{% elif 6 < hour <= 8 or 18 <= hour < 22 %}
Final: {{ (base * 0.7) | round(0) }} (after 0.7x)
{% else %}
Final: {{ [50, base] | min }}
{% endif %}

