--- SUNSET BOOST FORMULA CHECK ---
{% set elevation = state_attr('sun.sun', 'elevation') | float(90) %}
{% set e = 2.71828 %}
{% if 8 >= elevation >= -8 %}
  {% set gauss = e ** ( - (elevation ** 2) / (2 * (4 ** 2)) ) %}
