{% set lux = states('sensor.living_room_presence_light_sensor_light_level') | float(2000) %}
{% set weather = states('weather.home') %}
{% set season = 'winter' if now().month in [11, 12, 1, 2, 3] else 'summer' %}

{# Lux-based boost #}
{% set lux_boost = 0 %}
{% if lux < 100 %}
  {% set lux_boost = 18 %}
{% elif lux < 300 %}
  {% set lux_boost = 15 %}
{% elif lux < 700 %}
  {% set lux_boost = 10 %}
{% endif %}

{% set weather_lookup = { 'fog': 20, 'pouring': 18, 'cloudy': 10, 'partlycloudy': 5 } %}
{% set weather_boost = weather_lookup.get(weather, 0) %}
{% set season_boost = 8 if season == 'winter' else 0 %}

{# Sun boost #}
{% set elevation = state_attr('sun.sun', 'elevation') | float(90) %}
{% set rising = state_attr('sun.sun', 'rising') | default(true) %}
{% set sun_boost = 0 %}
{% if -8 <= elevation <= 8 %}
  {% set e = 2.71828 %}
  {% set gauss = e ** (-(elevation ** 2) / (2 * (4 ** 2))) %}
  {% if rising %}{% set sun_boost = (gauss * 3) | round(0) %}{% else %}{% set sun_boost = (gauss * 8) | round(0) %}{% endif %}
{% endif %}

{% set base_boost = lux_boost + (weather_boost * 0.4) + (season_boost * 0.25) + sun_boost %}

{# Time-based cutoff #}
{% set current_time = now() %}
{% set evening_cutoff = today_at('18:30') %}
{% set morning_cutoff = today_at('06:30') %}
{% if current_time >= evening_cutoff or current_time < morning_cutoff or lux > 2500 %}
  {% set base_boost = 0 %}
{% endif %}

