variables.final_min_k:
{% if repeat.item.id == 'recessed_ceiling' %}
  {{ repeat.item.min_k }}
{% else %}
  {% set proposed_min_k = repeat.item.min_k + base_warmth_offset + night_warmth_with_sensitivity %}
  {% if repeat.item.id in govee_zones %}
    {{ [2700, [6500, proposed_min_k] | min] | max }}
  {% else %}
    {{ [repeat.item.min_k, [repeat.item.max_k, proposed_min_k] | min] | max }}
  {% endif %}
{% endif %}

variables.final_max_k:
{% if repeat.item.id == 'recessed_ceiling' %}
  {{ repeat.item.max_k }}
{% else %}
  {% set proposed_max_k = repeat.item.max_k + base_warmth_offset + night_warmth_with_sensitivity %}
  {% if repeat.item.id in govee_zones %}
    {% set clamped_k = [2700, [6500, proposed_max_k] | min] | max %}
  {% else %}
    {% set clamped_k = [repeat.item.min_k, [repeat.item.max_k, proposed_max_k] | min] | max %}
  {% endif %}
  {{ [final_min_k | float, clamped_k] | max }}
{% endif %}
